mapper.select:
SELECT articleno, title, regdate, hit, attachfileNum, commentNum FROM ( SELECT rownum r,a.* FROM ( SELECT a.articleno, a.title, a.regdate, a.hit, m.name, count(distinct(f.attachfileno)) attachfileNum, count(distinct(c.commentno)) commentNum FROM article a, attachfile f, comments c, member m WHERE a.articleno = f.articleno(+) AND a.articleno = c.articleno(+) AND a.email = m.email(+) AND a.boardcd = #{boardCd}
AND (title LIKE '%${searchWord}%' OR DBMS_LOB.INSTR(content, '${searchWord}') > 0)
GROUP BY a.articleno, title, a.regdate, hit, m.name ORDER BY articleno DESC ) a ) WHERE r BETWEEN #{start} AND #{end}

mapper.update:
UPDATE article 
SET title = #{title}, content = #{content} 
WHERE articleno = #{articleNo}

mapper.select:
SELECT articleno, title FROM (SELECT rownum r,a.* FROM (SELECT articleno, title FROM article WHERE boardCd = #{boardCd} AND articleno > #{articleNo}
AND (title LIKE '%${searchWord}%' OR DBMS_LOB.INSTR(content, '${searchWord}') > 0)
ORDER BY articleno) a) WHERE r = 1

SELECT articleno, title FROM (SELECT rownum r,a.* FROM (SELECT articleno, title FROM article WHERE boardCd = #{boardCd} AND articleno < #{articleNo}
AND (title LIKE '%${searchWord}%' OR DBMS_LOB.INSTR(content, '${searchWord}') > 0)
ORDER BY articleno DESC) a) WHERE r = 1
