mapper.select:
SELECT articleno, title, regdate, hit, attachfileNum, commentNum FROM ( SELECT rownum R,A.* FROM ( SELECT a.articleno, a.title, a.regdate, count(distinct(v.no)) hit, count(distinct(f.attachfileno)) attachfileNum, count(distinct(c.commentno)) commentNum FROM article a left join attachfile f on a.articleno = f.articleno left join comments c on a.articleno = c.articleno left join views v on a.articleno = v.articleNo WHERE a.boardcd = #{boardCd}
AND (title LIKE '%${searchWord}%' OR DBMS_LOB.INSTR(content, '${searchWord}') > 0)
GROUP BY a.articleno, title, a.regdate ORDER BY articleno DESC ) A ) WHERE R BETWEEN #{start} AND #{end}

mapper.update:
UPDATE article 
SET title = #{title}, content = #{content} 
WHERE articleno = #{articleNo}

mapper.select:
SELECT articleno, title FROM (SELECT rownum r,a.* FROM (SELECT articleno, title FROM article WHERE boardCd = #{boardCd} AND articleno > #{articleNo}
AND (title LIKE '%${searchWord}%' OR DBMS_LOB.INSTR(content, '${searchWord}') > 0)
ORDER BY articleno) a) WHERE r = 1

SELECT articleno, title FROM (SELECT rownum r,a.* FROM (SELECT articleno, title FROM article WHERE boardCd = #{boardCd} AND articleno < #{articleNo}
AND (title LIKE '%${searchWord}%' OR DBMS_LOB.INSTR(content, '${searchWord}') > 0)
ORDER BY articleno DESC) a) WHERE r = 1

SELECT count(*) FROM views 
WHERE 
	articleNo = #{articleNo}
