mapper.select:
SELECT m.email, passwd, name, mobile, authority
FROM member m, authorities a
WHERE m.email = a.email AND m.email = #{email}

SELECT * FROM ( SELECT ROWNUM R,a.* FROM ( SELECT m.email, passwd, name, mobile, authority FROM member m, authorities a WHERE m.email = a.email
AND ( m.email LIKE '%' || #{search} || '%' OR name LIKE '%' || #{search} || '%' OR mobile LIKE '%' || #{search} || '%' OR authority LIKE '%' || #{search} || '%' )
ORDER BY authority ASC, name ASC ) a ) WHERE R BETWEEN #{startRecord} AND #{endRecord}

SELECT authority
FROM authorities
WHERE email = #{email}
