mapper.select:
SELECT articleno, title, regdate, hit, attachfileNum, commentNum FROM ( SELECT rownum r,a.* FROM ( SELECT a.articleno articleno, a.title, a.regdate, a.hit, count(distinct(f.attachfileno)) attachfileNum, count(distinct(c.commentno)) commentNum FROM article a, attachfile f, comments c WHERE a.articleno = f.articleno(+) AND a.articleno = c.articleno(+) AND a.boardcd = #{boardCd}
AND (title LIKE '%${searchWord}%' OR content LIKE '%${searchWord}%')
GROUP BY a.articleno, title, a.regdate, hit ORDER BY articleno DESC ) a ) WHERE r BETWEEN #{start} AND #{end}

SELECT count(*) FROM article WHERE boardcd = #{boardCd}

	AND (title LIKE '%${searchWord}%' OR
	content LIKE '%${searchWord}%')

SELECT attachfileno, filename, filetype, filesize, articleno
FROM attachfile
WHERE attachfileno = #{attachFileNo}

mapper.update:
UPDATE article 
SET title=#{title},content=#{content} 
WHERE articleno=#{articleNo}

mapper.select:
SELECT articleno, title, content, email, hit, regdate
FROM article
WHERE articleno = #{articleNo}

SELECT articleno, title FROM (SELECT rownum r,a.* FROM (SELECT articleno, title FROM article WHERE boardCd = #{boardCd} AND articleno > #{articleNo}
AND (title LIKE '%${searchWord}%' OR content LIKE '%${searchWord}%')
ORDER BY articleno) a) WHERE r = 1

SELECT articleno, title FROM (SELECT rownum r,a.* FROM (SELECT articleno, title FROM article WHERE boardCd = #{boardCd} AND articleno < #{articleNo}
AND (title LIKE '%${searchWord}%' OR content LIKE '%${searchWord}%')
ORDER BY articleno DESC) a) WHERE r = 1

SELECT commentno,articleno,email,memo,regdate 
FROM comments 
WHERE commentno = #{commentNo}
